mapper.select:
SELECT file_id AS fileId, file_name AS fileName, file_md5 AS fileMd5, file_status AS fileStatus
FROM uploadfile.upload_file
WHERE upload_file.file_md5=#{fileMd5}

mapper.update:
  UPDATE uploadfile.upload_file
  SET upload_file.file_status=#{fileStatus}
WHERE upload_file.file_id=#{fileId}
